  render() {

    return (
    <div>
      <div class='list-wrapper'>
        {this.state.games.map(game => (
          <div class='game-wrapper'>
            <img alt='game' class='image-style' src={game.thumb}/>
            <p>{game.salePrice}</p>
            <p class='strike'>{game.normalPrice}</p>
          </div>
        ))}
      </div>
    </div>
    );

  }
}
