import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import logo from '../logo.svg';
import '../App.css';
import axios from 'axios';
import "../../node_modules/jquery/dist/jquery.min.js";
import "../../node_modules/bootstrap/dist/js/bootstrap.min.js";

export default class Navbar extends Component {
  constructor() {
    super();
    this.logout = this.logout.bind(this);
  }

  logout(event) {
    event.preventDefault();
    axios.post('/user/logout').then(response => {
      if (response.status === 200) {
        this.props.updateUser({
          loggedIn: false,
          username: null
        });
      }
    }).catch(error => {
        console.log('Logout error');
    });
  }

  render() {
    const loggedIn = this.props.loggedIn;

    return (
      <nav class="navbar navbar-expand-xl" id="nav-container">
        <div class="navbar-brand">
          <h1 class="App-title">Steam Game Discounts</h1>
        </div>
        <button class="navbar-toggler" type="button" data-toggle="collapse" data-target="#navbarNav" aria-controls="navbarNav" aria-expanded="false" aria-label="Toggle navigation">
          <span class="navbar-toggler-icon"></span>
        </button>
        <div class="collapse navbar-collapse justify-content-end" id='navbarNav'>
          {loggedIn ? (
          <ul class="navbar-nav">
            <li class="nav-item m-3">
              <Link to="/" class="nav-link btn btn-link text-secondary">Home</Link>
            </li>
            <li class="nav-item m-3">
              <Link to='/games' class="nav-link btn btn-link text-secondary">My Games</Link>
            </li>
            <li class="nav-item m-3">
              <Link to="#" class="nav-link btn btn-link text-secondary" onClick={this.logout}>Logout</Link>
            </li>
          </ul>
          ) : (
          <div class="navbar-section">
            <Link to="/" class="btn btn-link text-secondary">
              <span class="text-secondary">Home</span>
            </Link>
            <Link to='/games' class="btn btn-link text-secondary">
              <span class="text-secondary">My Games</span>
            </Link>
            <Link to="/login" class="btn btn-link text-secondary">
              <span class="text-secondary">Login</span>
            </Link>
            <Link to="/signup" class="btn btn-link">
              <span class="text-secondary">Sign Up</span>
            </Link>
          </div>
          )}
        </div>
      </nav>
    );
  }
}

