import React, { Component } from 'react';
import fetch from 'node-fetch';
// components
import GameList from './shared/game-list';

const options = {method: 'GET', headers: {accept: 'application/json'}};

export default class Home extends Component {

  constructor() {
    super();

  }

  async getAllGames() {
    const response = await fetch('https://www.cheapshark.com/api/1.0/deals?', options);
    const data = await response.json();
    console.log(data);
  }

  render() {
    const imageStyle = {
      width: 400
    };

    return (
    <div>
      <GameList games={this.getAllGames()} isHome={true}/>
      <p>It's good to be home</p>
        <img alt="foo" style={imageStyle} src="https://i.ytimg.com/vi/N1icEHtgb3g/maxresdefault.jpg" />
    </div>
    );

  }
}
