import React, { Component } from 'react';
import fetch from 'node-fetch';
import './filter.css'

const options = {method: 'GET', headers: {accept: 'application/json'}};

export default class SearchFilter extends Component {
  constructor() {
    super();
    this.state = {
      stores: null
    };
  }

  componentDidMount() {
    fetch('https://www.cheapshark.com/api/1.0/stores', options)
    .then((response) => response.json())
    .then(storeList => {
      this.setState({ stores: storeList }, () => { console.log(this.state.stores) });
    });
  }

  render() {
    return (
      <div class='container-item'>
        <h3>Search Filters</h3>
        <br/>
        <div class='list'>
          <h4>Stores</h4>
          <i>(Defult searches all stores)</i>
          {this.state.stores?.map(store => ( 
            <div class='list-item'>
              <input type='checkbox'></input>
              <img alt='store' class='icon' src={'https://www.cheapshark.com'+ store.images.icon}/>
              <p class='text'>{store.storeName}</p>
            </div>
          ))}
        </div>
      </div>
    );
  }
}
