import React, { Component } from 'react';
// import GameList from './shared/game-list';
import axios from 'axios';
import './home.css';

export default class Games extends Component {

  constructor() {
    super();
    this.state = {
      games: null
    };

    // this.getUser = this.getUser.bind(this);
    // this.componentDidMount = this.componentDidMount.bind(this);
    // this.updateUser = this.updateUser.bind(this);
    this.fetchWishlist = this.fetchWishlist.bind(this);
  }

  async componentDidMount() {
    console.log(this.props.username);
    this.fetchWishlist();
    // this.getUser();
  }

  // async getUser() {
  //   axios.get('/user/').then(response => {
  //     console.log('Get user response: ');
  //     console.log(response.data);
  //     if (response.data.user) {
  //       console.log('Get User: There is a user saved in the server session: ');

  //       this.setState({
  //         loggedIn: true,
  //         username: response.data.user.username
  //       });
  //     } else {
  //       console.log('Get user: no user');
  //       this.setState({
  //         loggedIn: false,
  //         username: null
  //       });
  //     }
  //   });
  // }

  async fetchWishlist() {
    try {
      let response = await axios.get('/game/', {username: this.props.username});
      response = response.data;
      console.log('deleted game', response);
      this.setState({ games: response }, () => { console.log(this.state.games) });
    } catch (error) {
      console.log(error);
    }
  }

  async removeFromWishlist(data, game) {
    try {
      let response = await axios.delete('/game/', {game: JSON.stringify(data)});
      response = response.data;
      this.setState({games: response}, () => {console.log(this.state.games)});
    } catch(error) {
      console.log(error);
    }
  }

  render() {
    return (
      <div>
        <div class='list-wrapper'>
          {this.state.games?.map(game => ( 
            <div class='game-wrapper'>
              <img alt='game' class='image-style' src={game.thumb}/>
              <p>{game.salePrice}</p>
              <p class='strike'>{game.normalPrice}</p>
              <button onClick={this.removeFromWishlist.bind(this, game)}>Remove</button>
            </div>
          ))}
        </div>
      </div>
    );
 }
}
