import React, { Component } from 'react';
import fetch from 'node-fetch';
// components
import GameList from './shared/game-list';

const options = {method: 'GET', headers: {accept: 'application/json'}};

export default class Home extends Component {


  // const [games, setGames] = useState([]);
  // const [error, setError] = useState('');

  // useEffect(() => {
  //   getAllGames();
  // },[]);

  // useEffect(() => {
  //   setTimeout(() => setError(''), 10000);
  // }, [error])

  constructor() {
    super();
    this.state = {
      games: [],
      error: null
    };
    this.getAllGames = this.getAllGames.bind(this);
  }

  async componentDidMount() {
    await this.getAllGames();
  }

  async getAllGames() {
    try {
      const response = await fetch('https://www.cheapshark.com/api/1.0/deals?', options);
      const data = await response.json();
      console.log(data);
      this.setState({ games: data, error: null });
    } catch(err) {
      this.setState({ games: [], error: 'Error finding discounts: ' + err });
    }
    // console.log(data);
  }

  render() {
    const imageStyle = {
      width: 400
    };

    return (
    <div>
      <p>It's good to be home</p>
        <img alt="foo" style={imageStyle} src="https://i.ytimg.com/vi/N1icEHtgb3g/maxresdefault.jpg" />
    </div>
    );

  }
}
