import React, { Component } from 'react';
import fetch from 'node-fetch';
import './home.css';
import axios from 'axios';
// components
import GameList from './shared/game-list';

const options = {method: 'GET', headers: {accept: 'application/json'}};

export default class Home extends Component {


  // const [games, setGames] = useState([]);
  // const [error, setError] = useState('');

  // useEffect(() => {
  //   getAllGames();
  // },[]);

  // useEffect(() => {
  //   setTimeout(() => setError(''), 10000);
  // }, [error])

  constructor() {
    super();
    this.state = {
      games: null
    };
    this.fetchGame = this.fetchGame.bind(this);
  }

  componentDidMount() {
    fetch('https://www.cheapshark.com/api/1.0/deals?storeID=1,2,3,4,5', options)
    .then((response) => response.json())
    .then(gameList => {
      this.setState({ games: gameList }, () => { console.log(this.state.games) });
    });
  }

  fetchGame = async(data) => {
    const title = data.target.value;
    console.log(data.target.value);
    fetch('https://www.cheapshark.com/api/1.0/deals?onSale=1&storeID=1&title=' + title)
    .then((response) => response.json())
    .then(gameList => {
      this.setState({ games: gameList }, () => { console.log(this.state.games) });
    });
  }

  addToWishlist = async(data) => {
    console.log(data);
    try {
      axios.post('/user/game/' + data);
    } catch(error) {
      console.log('Error: ' + error);
    }
  }
  // async getAllGames() {
  //   try {
  //     const response = await fetch('https://www.cheapshark.com/api/1.0/deals?', options);
  //     const data = await response.json();
  //     console.log(data);
  //     this.setState({ games: data, error: null });
  //     console.log(this.state)
  //   } catch(err) {
  //     this.setState({ games: [], error: 'Error finding discounts: ' + err });
  //   }
  //   // console.log(data);
  // }

  render() {

    return (
    <div>
      <form>
        <input type='text' placeholder='Search' onChange={this.fetchGame}/>
      </form>
      <div class='list-wrapper'>
        {this.state.games?.map(game => (
          <div class='game-wrapper'>
            <img alt='game' class='image-style' src={game.thumb}/>
            <p>{game.salePrice}</p>
            <p class='strike'>{game.normalPrice}</p>
            <button onClick={this.addToWishilist} value={game}>Add to Wishlist</button>
          </div>
        ))}
      </div>
    </div>
    );
  }
}
